from re import match as m
c,b=lambda f,z:10*(ds:=lambda d:int(d) if len(d)==1 else b.index(d))(f)+ds(z),[r'\d','one','two','three','four','five','six','seven','eight','nine']
print(sum(map(lambda g:c(g[1].group(1),g[1].group(2)) if g[1] else c(g[2].group(1),g[2].group(1)) if g[2] else 0,[(a:='|'.join(b),m(f'^.*?({a}).*({a})(?!{a}).*$',l),m(f'^.*?({a})(?!{a}).*$',l)) for l in open('input.txt', 'r').readlines()])))